// ## Direction = Down
//
// - halign = start
//
// ![](down-start.png)
//
// - halign = center
//
// ![](down-center.png)
//
// - halign = end
//
// ![](down-end.png)
//
// ## Direction = Up
//
// - halign = start
//
// ![](up-start.png)
//
// - halign = center
//
// ![](up-center.png)
//
// - halign = end
//
// ![](up-end.png)
//
// ## Direction = Left
//
// - valign = start
//
// ![](left-start.png)
//
// - valign = center
//
// ![](left-center.png)
//
// - valign = end
//
// ![](left-end.png)
//
// ## Direction = Right
//
// - valign = start
